import Layout from '../components/Layout';
import Link from 'next/link';
import fetch from 'isomorphic-unfetch';

const getBlogs = () => {
    return [
        { id: 'hello-nextjs', title: 'Hello Next.js' },
        { id: 'learn-nextjs', title: 'Learn Next.js is awesome' },
        { id: 'deploy-nextjs', title: 'Deploy apps with ZEIT' }
    ];
};

const BlogLink = ({ blog }) => (
    <li key={blog.id}>
        <Link href={"/b/[id]"} as={`/b/${blog.id}`}>
            <a>{blog.title}</a>
        </Link>
    </li>
);

const Index = props => (
    <Layout>
        <p>Hello Next.js</p>
        <button className="btn btn-primary">
            button test
        </button>
        <ul>
            {/* child component 인 BlogLink 에는 css 가 적용되지 않는다. */}
            {/* 필요하면 해당 컴포넌트에서 스타일을 직접 주면 된다. */}
            {/* jsx global 을 사용하면 하위 컴포넌트로도 적용된다. */}
            {/* jsx global 을 _app.js 에서 사용하면 전역으로 사용 가능하다. */}
            {getBlogs().map(blog => (
                <BlogLink key={blog.id} blog={blog}/>
            ))}
        </ul>
        <style jsx>{`
            h1,
            a {
                font-family: 'Arial';
            }
            ul {
                padding: 0;
            }
            li {
            list-style: none;
            margin: 5px 0;
            }
            a {
              text-decoration: none;
              color: #c88;
            }
        `}</style>
        <h1>Batman TV Shows</h1>
        <ul>
            {props.shows.map(show => (
                <li key={show.id}>
                    <Link href={"/p/[id]"} as={`/p/${show.id}`}>
                        <a>{show.name}</a>
                    </Link>
                </li>
            ))}
        </ul>
    </Layout>
);

// `getInitialProps` can only be added to the default component exported by a page
// `getInitialProps` needs to work on both server and client
Index.getInitialProps = async function() {
    const res = await fetch("https://api.tvmaze.com/search/shows?q=batman");
    const data = await res.json();

    console.log(`Show data fetched. Count: ${data.length}`);

    return {
        shows: data.map(entry => entry.show)
    };
};

export default Index;
