//     projectId: "myxd-247f4",
//     storageBucket: "myxd-247f4.appspot.com",
//     messagingSenderId: "575641093519",
//     appId: "1:575641093519:web:56035054fbaa244dfe37fa"
// };

// console.log(config.option.firestore);
// console.log(config.option.firestore.projectId);

export function loadDB() {
    try {
        firebase.initializeApp(config.option.firestore);
    } catch (err) {
        // we skip the "already exists" message which is
        // not an actual error when we're hot-reloading
        if (!/already exists/.test(err.message)) {
            console.error('Firebase initialization error', err.stack);
        }
    }

    return firebase.firestore();
}
