import Head from "next/head";
import Layout from '../components/Layout';
import Link from 'next/link';
import fetch from 'isomorphic-unfetch';
import { loadDB } from '../lib/js/db';

import { createContext, useContext, useEffect, useState } from 'react';

const Index = props => {
    const [ data, setData ] = useState({});

    useEffect(() => {
        // 마운트 시에만 실행
    }, []);

    useEffect(() => {
        // 렌더링 될 때마다 실행
    });

    const refresh = async () => {
        const db = await loadDB();
        const querySnapshot = await db.collection('zz').get();
        querySnapshot.forEach(doc => {
            data = {...doc.data()};
        });

        console.log('is updating..');

        return { data };
    };

    return (
        <Layout>
            <Head>
                <title>MyXD - Index</title>
            </Head>
            <h1>This is Main Page</h1>
            <button className={"btn btn-warning text-white"} onClick={setData()}>refresh</button>
            <ul>
                {props.data.map((item, idx) => (
                    <li key={idx}>
                        {item.zzz}
                    </li>
                ))}
            </ul>

            <button className="btn btn-default">get</button>

            <style jsx>{`
                h1 {
                    font-color: #333;
                }
            `}</style>

        </Layout>
    );
};

Index.getInitialProps = async function() {
    const db = await loadDB();
    let data = [];
    const querySnapshot = await db.collection('zz').get();
    querySnapshot.forEach(doc => {
        data.push(doc.data());
    });

    return { data };
};

export default Index;
